#!/usr/bin/env python3
# -*- coding: utf-8 -*-

"""
Мини-помощник счастья
Поднимает настроение и дарит улыбки
"""

import random

def main():
    print("\n╔════════════════════════════════════════╗")
    print("║   🎈 ПОМОЩНИК СЧАСТЬЯ 🎈              ║")
    print("╚════════════════════════════════════════╝\n")
    
    # Комплименты
    compliments = [
        "У тебя прекрасная улыбка! 😊",
        "Ты вдохновляешь других! 🌟",
        "Твоя доброта делает мир лучше! 💝",
        "У тебя невероятная сила духа! 💪",
        "Ты умеешь находить радость в мелочах! 🌈"
    ]
    
    print("  🎁 Комплимент для тебя:")
    print(f"     {random.choice(compliments)}\n")
    
    # Полезные напоминания
    reminders = [
        "💧 Не забудь выпить воды!",
        "🧘 Сделай небольшую паузу и потянись!",
        "📱 Отложи телефон и посмотри в окно!",
        "🌿 Сделай несколько глубоких вдохов!",
        "😌 Похвали себя за сегодняшние успехи!"
    ]
    
    print("  ⏰ Напоминание:")
    print(f"     {random.choice(reminders)}\n")
    
    # Факты о счастье
    happiness_facts = [
        "Улыбка активирует 'гормоны счастья' в мозге!",
        "Благодарность повышает уровень счастья на 25%!",
        "Помощь другим делает нас счастливее!",
        "Природа снижает стресс и улучшает настроение!",
        "Музыка может мгновенно поднять настроение!"
    ]
    
    print("  📚 Интересный факт:")
    print(f"     {random.choice(happiness_facts)}\n")
    
    print("╔════════════════════════════════════════╗")
    print("║  Помни: ты достоин счастья каждый день!║")
    print("╚════════════════════════════════════════╝\n")

if __name__ == "__main__":
    main()
